  return (
    <div>
      <Header />
<div><InputArea/></div>

      {notes.map(keepernotes =>
        <Note
        key={keepernotes.key}
        title={keepernotes.title}
        content={keepernotes.content}/>
      )}

      <Footer />
    </div>
  );
}

export default App;


