  function addItem(inputText) {
    setItems((prevItems) => {
      return [...prevItems, inputText];
    });
  }

  return (
    <div>
      <Header />
      <InputArea addOn={""}/>
      <div>
        {" "}
        {notes.map(keepernotes => (
          <Note
            key={keepernotes.key}
            title={keepernotes.title}
            content={keepernotes.content}
          />
        ))}
      </div>

      <Footer />
    </div>
  );
}

export default App;

