  return (
    <div>
      <Header />
      <InputArea />
      <div>
        {" "}
        {notes.map(keepernotes => (
          <Note
            key={keepernotes.key}
            title={keepernotes.title}
            content={keepernotes.content}
          />
        ))}
      </div>

      <Footer />
    </div>
  );
}

export default App;

